import React, {useEffect, useState} from "react";
import {Map, MapMarker} from "react-kakao-maps-sdk";
import {Paper, IconButton, InputBase, Box, Grid} from "@mui/material";
import SearchIcon from "@mui/icons-material/Search";
import Logo from "../assets/logo.png";

interface MarkerListType {
	position: {
		lat: number,
		lng: number
	},
	content: string,
	addrName: string
}


const List:React.FC = () => {
	const [info, setInfo] = useState();
	const [markers, setMarkers] = useState<MarkerListType[]>([]);
	const [map, setMap] = useState<any>();
	const [input, setInput] = useState<string>("");

	const ps = new kakao.maps.services.Places();
	const geocoder = new kakao.maps.services.Geocoder();
	const bounds = new kakao.maps.LatLngBounds();

	const handleInput = (event:React.ChangeEvent<HTMLInputElement>) => {
		setInput(event.target.value);
	}

	const handleKeydown = (event:React.KeyboardEvent<HTMLInputElement>) => {
		if(event.key === "Enter")
			search();
	}

	// 검색
	const search = () => {
		if (!map) return;

		geocoder.addressSearch(input, (data:any, status:any) => {
			if(status === kakao.maps.services.Status.OK) {


				addSearchAddr(data)
			}
		});


		setMarkers(markers);
		map.setBounds(bounds)

		/*ps.keywordSearch(input, (data:any, status:any) => {
			if(status === kakao.maps.services.Status.OK) {
				const bounds = new kakao.maps.LatLngBounds();
				let markerArr = [];

				for (let i = 0; i < data.length; i++) {
					const isDuplicate = markerArr.some(item => item.addrName === data[i].address_name);

					if(isDuplicate)
						continue;

					markerArr.push({
						position: {
							lat: data[i].y,
							lng: data[i].x
						},
						content: data[i].place_name,
						addrName:data[i].address_name
					});

					bounds.extend(new kakao.maps.LatLng(data[i].y, data[i].x))
				}

				setMarkers(markerArr);
				map.setBounds(bounds)
			}
		})*/
	}

	const addSearchAddr = (data:any) => {
		for (let i = 0; i < data.length; i++) {
			const isDuplicate = markers.some(item => item.addrName === data[i].address_name);

			if(isDuplicate)
				continue;

			markers.push({
				position: {
					lat: data[i].y,
					lng: data[i].x
				},
				content: data[i].place_name,
				addrName:data[i].address_name
			});

			bounds.extend(new kakao.maps.LatLng(data[i].y, data[i].x))
		}
	}


	return (
		<Box
			component="section"
			sx={{height:"100vh"}}
		>
			<Box component="div">
				<Box component="div" sx={{p:2}}>
					<img src={Logo} alt="" style={{maxWidth:"100px"}}/>
				</Box>
				<Paper
					component="div" variant="outlined"
					sx={{display: "flex", alignItems: "center", width: "100%"}}
				>
					<InputBase
						sx={{pl:2, pr:2, flex: 1}}
						placeholder="장소를 입력해 주세요."
						onInput={handleInput}
						onKeyDown={handleKeydown}
					/>
					<IconButton
						type="button" sx={{p: "10px"}}
						aria-label="검색하기"
						onClick={search}
					>
						<SearchIcon />
					</IconButton>
				</Paper>
			</Box>

			<Map
				center={{lat: 33.5563, lng: 126.79581}}
				style={{width: "100%", height: "calc(100% - 55px)", borderRadius:"4px"}}
				onCreate={setMap}
			>
				{
					markers.map((marker) => (
						<MapMarker
							key={`marker-${marker.content}-${marker.position.lat},${marker.position.lng}`}
							position={marker.position}
						>
						</MapMarker>
					))
				}
			</Map>
		</Box>
	)
}


export default List;
